  ||isEmptyBlankOrNull(user.password)
  ||isEmptyBlankOrNull(user.firstname)
  ||isEmptyBlankOrNull(user.lastname)
  ||isEmptyBlankOrNull(user.email)
  ) {
    return false;
  }
  return true;
}

export default validateUser;

